import express, { type Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertUserSchema, insertBadgeSchema, insertBadgeApplicationSchema, insertEvidenceSchema } from "@shared/schema";
import multer from "multer";
import path from "path";
import fs from "fs";

// Configure multer for file uploads
const uploadDir = path.join(process.cwd(), 'uploads');
if (!fs.existsSync(uploadDir)) {
  fs.mkdirSync(uploadDir, { recursive: true });
}

const upload = multer({
  dest: uploadDir,
  limits: {
    fileSize: 10 * 1024 * 1024, // 10MB limit
  },
  fileFilter: (req, file, cb) => {
    // Accept images, videos, and documents
    const allowedTypes = /jpeg|jpg|png|gif|mp4|mov|avi|pdf|doc|docx|txt/;
    const extname = allowedTypes.test(path.extname(file.originalname).toLowerCase());
    const mimetype = allowedTypes.test(file.mimetype);

    if (mimetype && extname) {
      return cb(null, true);
    } else {
      cb(new Error('Only images, videos, and documents are allowed'));
    }
  }
});

export async function registerRoutes(app: Express): Promise<Server> {
  // Auth routes
  app.post("/api/auth/login", async (req, res) => {
    try {
      const { username, password } = req.body;

      if (!username || !password) {
        return res.status(400).json({ message: "Username and password are required" });
      }

      const user = await storage.getUserByUsername(username);
      if (!user || user.password !== password) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      // In a real app, you'd use proper session management here
      res.json({ user: { ...user, password: undefined } });
    } catch (error) {
      res.status(500).json({ message: "Internal server error" });
    }
  });

  app.post("/api/auth/register", async (req, res) => {
    try {
      const userData = insertUserSchema.parse(req.body);

      // Check if user already exists
      const existingUser = await storage.getUserByUsername(userData.username);
      if (existingUser) {
        return res.status(400).json({ message: "Username already exists" });
      }

      const existingEmail = await storage.getUserByEmail(userData.email);
      if (existingEmail) {
        return res.status(400).json({ message: "Email already exists" });
      }

      const user = await storage.createUser(userData);
      res.status(201).json({ user: { ...user, password: undefined } });
    } catch (error) {
      res.status(400).json({ message: "Invalid user data" });
    }
  });

  // User routes
  app.get("/api/users/:id", async (req, res) => {
    try {
      const user = await storage.getUser(req.params.id);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }
      res.json({ ...user, password: undefined });
    } catch (error) {
      res.status(500).json({ message: "Internal server error" });
    }
  });

  app.get("/api/users/:id/stats", async (req, res) => {
    try {
      const stats = await storage.getUserStats(req.params.id);
      res.json(stats);
    } catch (error) {
      res.status(500).json({ message: "Internal server error" });
    }
  });

  app.get("/api/users/:id/progress", async (req, res) => {
    try {
      const progress = await storage.getGraduateProfileProgress(req.params.id);
      res.json(progress);
    } catch (error) {
      res.status(500).json({ message: "Internal server error" });
    }
  });

  // Badge routes
  app.get("/api/badges", async (req, res) => {
    try {
      const { graduateProfile } = req.query;
      let badges;

      if (graduateProfile && typeof graduateProfile === 'string') {
        badges = await storage.getBadgesByGraduateProfile(graduateProfile as any);
      } else {
        badges = await storage.getAllBadges();
      }

      res.json(badges);
    } catch (error) {
      res.status(500).json({ message: "Internal server error" });
    }
  });

  app.get("/api/badges/:id", async (req, res) => {
    try {
      const badge = await storage.getBadge(req.params.id);
      if (!badge) {
        return res.status(404).json({ message: "Badge not found" });
      }
      res.json(badge);
    } catch (error) {
      res.status(500).json({ message: "Internal server error" });
    }
  });

  app.post("/api/badges", async (req, res) => {
    try {
      const badgeData = insertBadgeSchema.parse(req.body);
      const badge = await storage.createBadge(badgeData);
      res.status(201).json(badge);
    } catch (error) {
      res.status(400).json({ message: "Invalid badge data" });
    }
  });

  // Badge Application routes
  app.get("/api/users/:userId/applications", async (req, res) => {
    try {
      const applications = await storage.getUserApplications(req.params.userId);
      res.json(applications);
    } catch (error) {
      res.status(500).json({ message: "Internal server error" });
    }
  });

  app.get("/api/applications/:id", async (req, res) => {
    try {
      const application = await storage.getApplication(req.params.id);
      if (!application) {
        return res.status(404).json({ message: "Application not found" });
      }
      res.json(application);
    } catch (error) {
      res.status(500).json({ message: "Internal server error" });
    }
  });

  app.post("/api/applications", async (req, res) => {
    try {
      const applicationData = insertBadgeApplicationSchema.parse(req.body);
      const application = await storage.createApplication(applicationData);
      res.status(201).json(application);
    } catch (error) {
      res.status(400).json({ message: "Invalid application data" });
    }
  });

  app.patch("/api/applications/:id/status", async (req, res) => {
    try {
      const { status, reviewedBy, feedback } = req.body;
      const application = await storage.updateApplicationStatus(
        req.params.id,
        status,
        reviewedBy,
        feedback
      );
      if (!application) {
        return res.status(404).json({ message: "Application not found" });
      }
      res.json(application);
    } catch (error) {
      res.status(400).json({ message: "Invalid status update" });
    }
  });

  app.get("/api/applications/review", async (req, res) => {
    try {
      const applications = await storage.getApplicationsForReview();
      res.json(applications);
    } catch (error) {
      res.status(500).json({ message: "Internal server error" });
    }
  });

  // Evidence routes
  app.get("/api/applications/:applicationId/evidence", async (req, res) => {
    try {
      const evidence = await storage.getApplicationEvidence(req.params.applicationId);
      res.json(evidence);
    } catch (error) {
      res.status(500).json({ message: "Internal server error" });
    }
  });

  app.post("/api/evidence", upload.single('file'), async (req, res) => {
    try {
      const evidenceData = {
        ...req.body,
        metadata: req.file ? {
          originalName: req.file.originalname,
          size: req.file.size,
          mimetype: req.file.mimetype
        } : null,
        fileUrl: req.file ? `/uploads/${req.file.filename}` : null
      };

      const validatedData = insertEvidenceSchema.parse(evidenceData);
      const evidence = await storage.createEvidence(validatedData);
      res.status(201).json(evidence);
    } catch (error) {
      res.status(400).json({ message: "Invalid evidence data" });
    }
  });

  app.delete("/api/evidence/:id", async (req, res) => {
    try {
      const success = await storage.deleteEvidence(req.params.id);
      if (!success) {
        return res.status(404).json({ message: "Evidence not found" });
      }
      res.status(204).send();
    } catch (error) {
      res.status(500).json({ message: "Internal server error" });
    }
  });

  // Slide generation routes
  app.post("/api/slides/generate", async (req, res) => {
    try {
      const { badgeId, badgeName, graduateProfile, evidence, reflections } = req.body;

      // Get badge details
      const badge = await storage.getBadge(badgeId);
      if (!badge) {
        return res.status(404).json({ message: "Badge not found" });
      }

      // Generate slide content
      const slideData = {
        badgeId,
        badgeName,
        graduateProfile,
        badge,
        evidence,
        reflections,
        generatedAt: new Date().toISOString(),
        downloadUrl: `/api/slides/download/${badgeId}`,
        viewUrl: `/api/slides/view/${badgeId}`,
        shareUrl: `/api/slides/share/${badgeId}`
      };

      // In a real implementation, you would use PptxGenJS here
      // For now, we'll return the slide data structure
      res.json(slideData);
    } catch (error) {
      res.status(500).json({ message: "Error generating slides" });
    }
  });

  app.get("/api/slides/download/:badgeId", async (req, res) => {
    try {
      // In a real implementation, this would generate and serve the PPTX file
      res.json({ message: "Download functionality would be implemented here", badgeId: req.params.badgeId });
    } catch (error) {
      res.status(500).json({ message: "Error downloading slides" });
    }
  });

  app.get("/api/slides/view/:badgeId", async (req, res) => {
    try {
      // In a real implementation, this would serve an HTML presentation
      res.json({ message: "View functionality would be implemented here", badgeId: req.params.badgeId });
    } catch (error) {
      res.status(500).json({ message: "Error viewing slides" });
    }
  });

  app.get("/api/slides/share/:badgeId", async (req, res) => {
    try {
      // In a real implementation, this would provide sharing functionality
      res.json({ message: "Share functionality would be implemented here", badgeId: req.params.badgeId });
    } catch (error) {
      res.status(500).json({ message: "Error sharing slides" });
    }
  });

  // Serve uploaded files
  app.use('/uploads', express.static(uploadDir));

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import {
  users,
  badges,
  badgeApplications,
  evidence,
  type User,
  type InsertUser,
  type Badge,
  type InsertBadge,
  type BadgeApplication,
  type InsertBadgeApplication,
  type Evidence,
  type InsertEvidence,
  type GraduateProfile,
  type BadgeStatus
} from "@shared/schema";
import { db } from "./db";
import { eq, and, count, desc, sql } from "drizzle-orm";

export interface IStorage {
  // User methods
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Badge methods
  getAllBadges(): Promise<Badge[]>;
  getBadgesByGraduateProfile(profile: GraduateProfile): Promise<Badge[]>;
  getBadge(id: string): Promise<Badge | undefined>;
  createBadge(badge: InsertBadge): Promise<Badge>;
  updateBadge(id: string, badge: Partial<InsertBadge>): Promise<Badge | undefined>;

  // Badge Application methods
  getUserApplications(userId: string): Promise<(BadgeApplication & { badge: Badge; evidence: Evidence[] })[]>;
  getApplication(id: string): Promise<(BadgeApplication & { badge: Badge; user: User; evidence: Evidence[] }) | undefined>;
  createApplication(application: InsertBadgeApplication): Promise<BadgeApplication>;
  updateApplicationStatus(id: string, status: BadgeStatus, reviewedBy?: string, feedback?: string): Promise<BadgeApplication | undefined>;
  getApplicationsForReview(): Promise<(BadgeApplication & { badge: Badge; user: User })[]>;

  // Evidence methods
  getApplicationEvidence(applicationId: string): Promise<Evidence[]>;
  createEvidence(evidence: InsertEvidence): Promise<Evidence>;
  deleteEvidence(id: string): Promise<boolean>;

  // Stats methods
  getUserStats(userId: string): Promise<{
    earnedBadges: number;
    inProgress: number;
    totalBadges: number;
    completionRate: number;
  }>;
  getGraduateProfileProgress(userId: string): Promise<{
    profile: GraduateProfile;
    earnedCount: number;
    totalCount: number;
    percentage: number;
  }[]>;
}

export class DatabaseStorage implements IStorage {
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user || undefined;
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.username, username));
    return user || undefined;
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.email, email));
    return user || undefined;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await db.insert(users).values(insertUser).returning();
    return user;
  }

  async getAllBadges(): Promise<Badge[]> {
    return await db.select().from(badges).where(eq(badges.isActive, true)).orderBy(badges.graduateProfile, badges.level);
  }

  async getBadgesByGraduateProfile(profile: GraduateProfile): Promise<Badge[]> {
    return await db.select().from(badges)
      .where(and(eq(badges.graduateProfile, profile), eq(badges.isActive, true)))
      .orderBy(badges.level);
  }

  async getBadge(id: string): Promise<Badge | undefined> {
    const [badge] = await db.select().from(badges).where(eq(badges.id, id));
    return badge || undefined;
  }

  async createBadge(badge: InsertBadge): Promise<Badge> {
    const [newBadge] = await db.insert(badges).values(badge).returning();
    return newBadge;
  }

  async updateBadge(id: string, badge: Partial<InsertBadge>): Promise<Badge | undefined> {
    const [updatedBadge] = await db.update(badges).set(badge).where(eq(badges.id, id)).returning();
    return updatedBadge || undefined;
  }

  async getUserApplications(userId: string): Promise<(BadgeApplication & { badge: Badge; evidence: Evidence[] })[]> {
    const applications = await db.select()
      .from(badgeApplications)
      .leftJoin(badges, eq(badgeApplications.badgeId, badges.id))
      .leftJoin(evidence, eq(badgeApplications.id, evidence.applicationId))
      .where(eq(badgeApplications.userId, userId))
      .orderBy(desc(badgeApplications.createdAt));

    const groupedApplications = applications.reduce((acc, row) => {
      const appId = row.badge_applications.id;
      if (!acc[appId]) {
        acc[appId] = {
          ...row.badge_applications,
          badge: row.badges!,
          evidence: []
        };
      }
      if (row.evidence) {
        acc[appId].evidence.push(row.evidence);
      }
      return acc;
    }, {} as Record<string, BadgeApplication & { badge: Badge; evidence: Evidence[] }>);

    return Object.values(groupedApplications);
  }

  async getApplication(id: string): Promise<(BadgeApplication & { badge: Badge; user: User; evidence: Evidence[] }) | undefined> {
    const applicationData = await db.select()
      .from(badgeApplications)
      .leftJoin(badges, eq(badgeApplications.badgeId, badges.id))
      .leftJoin(users, eq(badgeApplications.userId, users.id))
      .leftJoin(evidence, eq(badgeApplications.id, evidence.applicationId))
      .where(eq(badgeApplications.id, id));

    if (applicationData.length === 0) return undefined;

    const application = applicationData[0].badge_applications;
    const badge = applicationData[0].badges!;
    const user = applicationData[0].users!;
    const evidenceList = applicationData.filter(row => row.evidence).map(row => row.evidence!);

    return {
      ...application,
      badge,
      user,
      evidence: evidenceList
    };
  }

  async createApplication(application: InsertBadgeApplication): Promise<BadgeApplication> {
    const [newApplication] = await db.insert(badgeApplications).values(application).returning();
    return newApplication;
  }

  async updateApplicationStatus(id: string, status: BadgeStatus, reviewedBy?: string, feedback?: string): Promise<BadgeApplication | undefined> {
    const updateData: any = { status };
    if (status === 'submitted') {
      updateData.submittedAt = new Date();
    }
    if (status === 'earned' || status === 'rejected') {
      updateData.reviewedAt = new Date();
      updateData.reviewedBy = reviewedBy;
      updateData.feedback = feedback;
    }

    const [updatedApplication] = await db.update(badgeApplications)
      .set(updateData)
      .where(eq(badgeApplications.id, id))
      .returning();
    return updatedApplication || undefined;
  }

  async getApplicationsForReview(): Promise<(BadgeApplication & { badge: Badge; user: User })[]> {
    const applications = await db.select()
      .from(badgeApplications)
      .leftJoin(badges, eq(badgeApplications.badgeId, badges.id))
      .leftJoin(users, eq(badgeApplications.userId, users.id))
      .where(eq(badgeApplications.status, 'submitted'))
      .orderBy(desc(badgeApplications.submittedAt));

    return applications.map(row => ({
      ...row.badge_applications,
      badge: row.badges!,
      user: row.users!
    }));
  }

  async getApplicationEvidence(applicationId: string): Promise<Evidence[]> {
    return await db.select().from(evidence)
      .where(eq(evidence.applicationId, applicationId))
      .orderBy(desc(evidence.createdAt));
  }

  async createEvidence(evidenceData: InsertEvidence): Promise<Evidence> {
    const [newEvidence] = await db.insert(evidence).values(evidenceData).returning();
    return newEvidence;
  }

  async deleteEvidence(id: string): Promise<boolean> {
    const result = await db.delete(evidence).where(eq(evidence.id, id));
    return result.rowCount ? result.rowCount > 0 : false;
  }

  async getUserStats(userId: string): Promise<{
    earnedBadges: number;
    inProgress: number;
    totalBadges: number;
    completionRate: number;
  }> {
    const [earnedResult] = await db.select({ count: count() })
      .from(badgeApplications)
      .where(and(eq(badgeApplications.userId, userId), eq(badgeApplications.status, 'earned')));

    const [inProgressResult] = await db.select({ count: count() })
      .from(badgeApplications)
      .where(and(
        eq(badgeApplications.userId, userId),
        sql`${badgeApplications.status} IN ('in_progress', 'submitted', 'in_review')`
      ));

    const [totalResult] = await db.select({ count: count() })
      .from(badges)
      .where(eq(badges.isActive, true));

    const earnedBadges = earnedResult.count;
    const inProgress = inProgressResult.count;
    const totalBadges = totalResult.count;
    const completionRate = totalBadges > 0 ? Math.round((earnedBadges / totalBadges) * 100) : 0;

    return {
      earnedBadges,
      inProgress,
      totalBadges,
      completionRate
    };
  }

  async getGraduateProfileProgress(userId: string): Promise<{
    profile: GraduateProfile;
    earnedCount: number;
    totalCount: number;
    percentage: number;
  }[]> {
    const profiles: GraduateProfile[] = ['excellence', 'innovation', 'integrity', 'inspiration', 'hauora', 'relationships'];
    const result = [];

    for (const profile of profiles) {
      const [earnedResult] = await db.select({ count: count() })
        .from(badgeApplications)
        .leftJoin(badges, eq(badgeApplications.badgeId, badges.id))
        .where(and(
          eq(badgeApplications.userId, userId),
          eq(badgeApplications.status, 'earned'),
          eq(badges.graduateProfile, profile)
        ));

      const [totalResult] = await db.select({ count: count() })
        .from(badges)
        .where(and(eq(badges.graduateProfile, profile), eq(badges.isActive, true)));

      const earnedCount = earnedResult.count;
      const totalCount = totalResult.count;
      const percentage = totalCount > 0 ? Math.round((earnedCount / totalCount) * 100) : 0;

      result.push({
        profile,
        earnedCount,
        totalCount,
        percentage
      });
    }

    return result;
  }
}

export const storage = new DatabaseStorage();

