import { useQuery } from "@tanstack/react-query";
import Header from "@/components/layout/header";
import { Badge, BadgeApplication, User } from "@shared/schema";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { GRADUATE_PROFILE_LABELS, GRADUATE_PROFILE_ICONS } from "@/types";

// Mock user ID - in a real app this would come from auth context
const MOCK_USER_ID = "1";

export default function ProfilePage() {
  // Fetch user data
  const { data: user } = useQuery<User>({
    queryKey: ['/api/users', MOCK_USER_ID],
  });

  // Fetch user applications
  const { data: applications = [] } = useQuery<(BadgeApplication & { badge: Badge; evidence: any[] })[]>({
    queryKey: ['/api/users', MOCK_USER_ID, 'applications'],
  });

  // Get earned badges
  const earnedBadges = applications.filter(app => app.status === 'earned');

  // Group earned badges by graduate profile
  const badgesByProfile = earnedBadges.reduce((acc, app) => {
    const profile = app.badge.graduateProfile;
    if (!acc[profile]) {
      acc[profile] = [];
    }
    acc[profile].push(app);
    return acc;
  }, {} as Record<string, (BadgeApplication & { badge: Badge; evidence: any[] })[]>);

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const getInitials = (firstName: string, lastName: string) => {
    return `${firstName.charAt(0)}${lastName.charAt(0)}`.toUpperCase();
  };

  if (!user) {
    return (
      <>
        <Header title="Profile" description="Your personal badge portfolio" />
        <div className="p-6">
          <div className="text-center">Loading...</div>
        </div>
      </>
    );
  }

  return (
    <>
      <Header title="Profile" description="Your personal badge portfolio" />

      <div className="p-6 overflow-y-auto h-full">
        {/* User Info Section */}
        <Card className="mb-8">
          <CardHeader>
            <CardTitle>Profile Information</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="flex items-center space-x-6">
              <Avatar className="w-24 h-24">
                <AvatarFallback className="text-2xl" data-testid="user-avatar">
                  {getInitials(user.firstName, user.lastName)}
                </AvatarFallback>
              </Avatar>
              <div className="flex-1">
                <h2 className="text-2xl font-bold mb-2" data-testid="user-name">
                  {user.firstName} {user.lastName}
                </h2>
                <p className="text-muted-foreground mb-1" data-testid="user-email">{user.email}</p>
                <p className="text-muted-foreground mb-4" data-testid="user-username">@{user.username}</p>
                <div className="flex items-center space-x-4 text-sm">
                  <span className="flex items-center space-x-1">
                    <i className="fas fa-trophy text-success"></i>
                    <span data-testid="earned-badges-count">{earnedBadges.length} badges earned</span>
                  </span>
                  <span className="flex items-center space-x-1">
                    <i className="fas fa-calendar"></i>
                    <span data-testid="join-date">Joined {formatDate(user.createdAt.toString())}</span>
                  </span>
                </div>
              </div>
              <Button variant="outline" data-testid="button-edit-profile">
                <i className="fas fa-edit mr-2"></i>
                Edit Profile
              </Button>
            </div>
          </CardContent>
        </Card>

        {/* Badge Showcase */}
        <div className="mb-8">
          <h2 className="text-2xl font-bold mb-6" data-testid="badge-showcase-title">Badge Showcase</h2>

          {earnedBadges.length === 0 ? (
            <Card>
              <CardContent className="py-12 text-center">
                <i className="fas fa-medal text-4xl text-muted-foreground mb-4"></i>
                <h3 className="text-lg font-medium mb-2">No badges earned yet</h3>
                <p className="text-muted-foreground mb-4">Start your badge journey by exploring available badges</p>
                <Button data-testid="button-browse-badges">
                  <i className="fas fa-search mr-2"></i>
                  Browse Badges
                </Button>
              </CardContent>
            </Card>
          ) : (
            <div className="space-y-8">
              {Object.entries(GRADUATE_PROFILE_LABELS).map(([profile, label]) => {
                const profileBadges = badgesByProfile[profile] || [];

                if (profileBadges.length === 0) return null;

                return (
                  <Card key={profile}>
                    <CardHeader>
                      <CardTitle className="flex items-center space-x-3">
                        <div className={`w-10 h-10 rounded-lg flex items-center justify-center graduate-profile-${profile}`}>
                          <i className={`${GRADUATE_PROFILE_ICONS[profile]} text-lg`}></i>
                        </div>
                        <span data-testid={`profile-section-${profile}`}>{label}</span>
                        <span className="text-sm font-normal text-muted-foreground">
                          ({profileBadges.length} badge{profileBadges.length !== 1 ? 's' : ''})
                        </span>
                      </CardTitle>
                    </CardHeader>
                    <CardContent>
                      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                        {profileBadges.map((application) => (
                          <div key={application.id} className="p-4 border border-border rounded-lg">
                            <div className="flex items-center justify-between mb-3">
                              <h4 className="font-semibold" data-testid={`earned-badge-${application.badge.id}`}>
                                {application.badge.name}
                              </h4>
                              <i className="fas fa-check-circle text-success"></i>
                            </div>
                            <p className="text-sm text-muted-foreground mb-3">
                              {application.badge.description}
                            </p>
                            <div className="text-xs text-muted-foreground">
                              Earned on {application.reviewedAt ? formatDate(application.reviewedAt.toString()) : 'Unknown date'}
                            </div>
                          </div>
                        ))}
                      </div>
                    </CardContent>
                  </Card>
                );
              })}
            </div>
          )}
        </div>

        {/* Recent Activity */}
        <Card>
          <CardHeader>
            <CardTitle data-testid="recent-activity-title">Recent Activity</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {applications
                .sort((a, b) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime())
                .slice(0, 5)
                .map((application, index) => (
                  <div key={application.id} className="flex items-center space-x-4 p-3 rounded-lg bg-muted/50">
                    <div className={`w-10 h-10 rounded-lg flex items-center justify-center graduate-profile-${application.badge.graduateProfile}`}>
                      <i className={`${GRADUATE_PROFILE_ICONS[application.badge.graduateProfile]} text-sm`}></i>
                    </div>
                    <div className="flex-1">
                      <p className="font-medium" data-testid={`activity-${index}-title`}>
                        {application.status === 'earned' ? 'Earned' : 'Applied for'} {application.badge.name}
                      </p>
                      <p className="text-sm text-muted-foreground" data-testid={`activity-${index}-date`}>
                        {formatDate(application.createdAt.toString())}
                      </p>
                    </div>
                    <div className={`px-2 py-1 rounded-full text-xs font-medium ${
                      application.status === 'earned' 
                        ? 'bg-success/10 text-success' 
                        : application.status === 'in_progress'
                        ? 'bg-primary/10 text-primary'
                        : 'bg-warning/10 text-warning'
                    }`}>
                      {application.status.replace('_', ' ').toUpperCase()}
                    </div>
                  </div>
                ))}

              {applications.length === 0 && (
                <p className="text-center text-muted-foreground py-8">
                  No activity yet. Start applying for badges to see your progress here.
                </p>
              )}
            </div>
          </CardContent>
        </Card>
      </div>
    </>
  );
}

client/src/pages/progress.tsx.content:
import { useQuery } from "@tanstack/react-query";
import Header from "@/components/layout/header";
import StatsOverview from "@/components/stats/stats-overview";
import GraduateProfileProgressComponent from "@/components/progress/graduate-profile-progress";
import { Progress } from "@/components/ui/progress";
import { Badge, BadgeApplication } from "@shared/schema";
import { UserStats, GraduateProfileProgress, GRADUATE_PROFILE_LABELS } from "@/types";

// Mock user ID - in a real app this would come from auth context
const MOCK_USER_ID = "1";

export default function ProgressPage() {
  // Fetch user stats
  const { data: stats = { earnedBadges: 0, inProgress: 0, totalBadges: 0, completionRate: 0 } } = useQuery<UserStats>({
    queryKey: ['/api/users', MOCK_USER_ID, 'stats'],
  });

  // Fetch graduate profile progress
  const { data: progress = [] } = useQuery<GraduateProfileProgress[]>({
    queryKey: ['/api/users', MOCK_USER_ID, 'progress'],
  });

  // Fetch user applications
  const { data: applications = [] } = useQuery<(BadgeApplication & { badge: Badge; evidence: any[] })[]>({
    queryKey: ['/api/users', MOCK_USER_ID, 'applications'],
  });

  // Fetch all badges
  const { data: badges = [] } = useQuery<Badge[]>({
    queryKey: ['/api/badges'],
  });

  // Group applications by graduate profile
  const applicationsByProfile = applications.reduce((acc, app) => {
    const profile = app.badge.graduateProfile;
    if (!acc[profile]) {
      acc[profile] = [];
    }
    acc[profile].push(app);
    return acc;
  }, {} as Record<string, (BadgeApplication & { badge: Badge; evidence: any[] })[]>);

  // Group badges by graduate profile
  const badgesByProfile = badges.reduce((acc, badge) => {
    const profile = badge.graduateProfile;
    if (!acc[profile]) {
      acc[profile] = [];
    }
    acc[profile].push(badge);
    return acc;
  }, {} as Record<string, Badge[]>);

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'earned':
        return 'text-success';
      case 'in_review':
      case 'submitted':
        return 'text-warning';
      case 'in_progress':
        return 'text-primary';
      default:
        return 'text-muted-foreground';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'earned':
        return 'fas fa-check-circle';
      case 'in_review':
        return 'fas fa-clock';
      case 'submitted':
        return 'fas fa-paper-plane';
      case 'in_progress':
        return 'fas fa-play-circle';
      default:
        return 'fas fa-circle';
    }
  };

  return (
    <>
      <Header 
        title="Progress" 
        description="Track your overall badge progress and achievements"
      />

      <div className="p-6 overflow-y-auto h-full">
        <StatsOverview stats={stats} />

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8 mb-8">
          <div className="lg:col-span-2">
            <GraduateProfileProgressComponent progress={progress} />
          </div>

          <div className="bg-card p-6 rounded-lg border border-border">
            <h2 className="text-xl font-bold mb-6" data-testid="achievement-milestones-title">Achievement Milestones</h2>
            <div className="space-y-4">
              <div className="flex items-center justify-between p-3 rounded-lg bg-muted/50">
                <div className="flex items-center space-x-3">
                  <i className="fas fa-medal text-secondary text-lg"></i>
                  <span className="font-medium">First Badge</span>
                </div>
                <i className={stats.earnedBadges > 0 ? "fas fa-check text-success" : "fas fa-lock text-muted-foreground"}></i>
              </div>

              <div className="flex items-center justify-between p-3 rounded-lg bg-muted/50">
                <div className="flex items-center space-x-3">
                  <i className="fas fa-fire text-warning text-lg"></i>
                  <span className="font-medium">5 Badges</span>
                </div>
                <i className={stats.earnedBadges >= 5 ? "fas fa-check text-success" : "fas fa-lock text-muted-foreground"}></i>
              </div>

              <div className="flex items-center justify-between p-3 rounded-lg bg-muted/50">
                <div className="flex items-center space-x-3">
                  <i className="fas fa-crown text-yellow-500 text-lg"></i>
                  <span className="font-medium">Profile Champion</span>
                </div>
                <i className={progress.some(p => p.percentage === 100) ? "fas fa-check text-success" : "fas fa-lock text-muted-foreground"}></i>
              </div>

              <div className="flex items-center justify-between p-3 rounded-lg bg-muted/50">
                <div className="flex items-center space-x-3">
                  <i className="fas fa-trophy text-yellow-500 text-lg"></i>
                  <span className="font-medium">Badge Master</span>
                </div>
                <i className={stats.completionRate >= 50 ? "fas fa-check text-success" : "fas fa-lock text-muted-foreground"}></i>
              </div>
            </div>
          </div>
        </div>

        {/* Detailed Progress by Graduate Profile */}
        <div className="space-y-8">
          {Object.entries(GRADUATE_PROFILE_LABELS).map(([profile, label]) => {
            const profileApplications = applicationsByProfile[profile] || [];
            const profileBadges = badgesByProfile[profile] || [];
            const earnedCount = profileApplications.filter(app => app.status === 'earned').length;
            const totalCount = profileBadges.length;
            const percentage = totalCount > 0 ? Math.round((earnedCount / totalCount) * 100) : 0;

            return (
              <div key={profile} className="bg-card p-6 rounded-lg border border-border">
                <div className="flex items-center justify-between mb-6">
                  <h3 className="text-xl font-bold" data-testid={`profile-section-${profile}`}>{label}</h3>
                  <div className="text-sm text-muted-foreground">
                    {earnedCount}/{totalCount} badges ({percentage}%)
                  </div>
                </div>

                <Progress value={percentage} className="mb-6" />

                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                  {profileBadges.map((badge) => {
                    const application = profileApplications.find(app => app.badgeId === badge.id);
                    return (
                      <div key={badge.id} className="p-4 border border-border rounded-lg">
                        <div className="flex items-center justify-between mb-2">
                          <h4 className="font-medium" data-testid={`badge-name-${badge.id}`}>{badge.name}</h4>
                          {application && (
                            <i className={`${getStatusIcon(application.status)} ${getStatusColor(application.status)}`}></i>
                          )}
                        </div>
                        <p className="text-sm text-muted-foreground mb-3" data-testid={`badge-description-${badge.id}`}>
                          {badge.description}
                        </p>
                        {application ? (
                          <div className="text-xs text-muted-foreground">
                            Status: <span className={getStatusColor(application.status)} data-testid={`badge-status-${badge.id}`}>
                              {application.status.replace('_', ' ').toUpperCase()}
                            </span>
                          </div>
                        ) : (
                          <div className="text-xs text-muted-foreground" data-testid={`badge-status-${badge.id}`}>
                            Not started
                          </div>
                        )}
                      </div>
                    );
                  })}
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </>
  );
}

